para.programlisting:
tr example::keg:1.0 {
#specify profiles that apply for all the sites for the transformation #in each site entry the profile can be overriden
profile env "APP_HOME" "/tmp/myscratch" profile env "JAVA_HOME" "/opt/java/1.6"
site isi { profile env "HELLo" "WORLD" profile condor "FOO" "bar" profile env "JAVA_HOME" "/bin/java.1.6" pfn "/path/to/keg" arch "x86" os "linux" osrelease "fc" osversion "4" type "INSTALLED" }
site wind { profile env "CPATH" "/usr/cpath" profile condor "universe" "condor" pfn "file:///path/to/keg" arch "x86" os "linux" osrelease "fc" osversion "4" type "STAGEABLE" } }
